import { useState } from 'react'

export default function BlogCreate(props) {
  const [formData, setFormData] = useState({
    name: ''
  })

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }))
  }

  return (
    <div>
    <form onSubmit={(e) => {
      e.preventDefault();
      props.handleCreate(formData);
    }}>
      <h3>New Blog Post</h3>
      <label>Game Title:
        <input
          type='text'
          name='name'
          value={formData.name}
          onChange={handleChange}
        />
      </label>
      <label>Review:
        <input
          type='text'
          name='name'
          value={formData.name}
          onChange={handleChange}
        />
      </label>
        <div>
         <Link> </Link> 
        </div>  
      </form>
    </div>
  )
}
