import { useState, useEffect } from 'react';
import { Switch, Route, useHistory } from 'react-router-dom';

import './App.css';
// import MainContainer from './container/MainContainer';
import Layout from './layouts/Layout';
import BlogCreate from './screens/BlogCreate';
import Blogs from './screens/Blogs';
import Login from './screens/Login';
import Register from './screens/Register'
import { loginUser, registerUser, removeToken, verifyUser } from './services/auth';
import {postBlog, getAllBlogs, destroyBlog} from './services/blogs'


function App() {
  const [blogs, setBlogs]= useState([])

  const [currentUser, setCurrentUser] = useState(null);
  const history = useHistory()

  useEffect(() => {
    const getBlogs = async () => {
      const blogData = await getAllBlogs()
      setBlogs(blogData)
    }
    getBlogs()
  },[])

  useEffect(() => {
    const handleVerify = async () => {
      const userData = await verifyUser();
      setCurrentUser(userData);
      if (!userData) {
        history.push('/')
      }
    }
    handleVerify();
  }, [history])

  const handleLogin = async (loginData) => {
    const userData = await loginUser(loginData);
    setCurrentUser(userData);
    history.push('/');
  }

  const handleRegister = async (registerData) => {
    const userData = await registerUser(registerData);
    setCurrentUser(userData);
    history.push('/');
  }

  const handleLogout = () => {
    setCurrentUser(null);
    localStorage.removeItem('authToken');
    removeToken();
    history.push('/');
  }

  const handleCreate = async (blogData) => {
    const newBlog = await postBlog(blogData)
    setBlogs(prevState => [...prevState, newBlog])
    history.push('/blogs')
  }

  const handleDelete = async (id) => {
    await destroyBlog(id)
    setBlogs(prevState => prevState.filter(blog=> blog.id ))
  }


  return (
    <div>
    <Layout
      currentUser={currentUser}
      handleLogout={handleLogout}
    >
      <Switch>

        <Route path='/login'>
          {/* <MainContainer currentUser={currentUser} /> */}
          <Login handleLogin={handleLogin} />
        </Route>

        <Route path='/register'>
          <Register handleRegister={handleRegister} />
        </Route>

        <Route path='/blogs/create'>
            <BlogCreate handleCreate={handleCreate}/>
        </Route>

        <Route path='/blogs'>
            <Blogs blogs={blogs} currentUser={currentUser}/>
        </Route>

        {/* <Route path='/'>
            <Blogs/>
        </Route>  */}



        <Route path='/create'><BlogCreate /></Route>


      </Switch>
      </Layout>
    </div>
  );
}

export default App;
