import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { getOneBlog } from '../services/blogs'
import { postComment } from '../services/comments'
import CommentCreate from './CommentCreate'

export default function BlogShow() {
  const [blog, setBlog] = useState({})
  const [comments, setComments] = useState([])
  const [commentToggle, setCommentToggle] = useState(false)
  const { id } = useParams()

  useEffect(() => {
    const getBlog = async () => {
      const blogData = await getOneBlog(id)
      console.log(blogData);
      setBlog(blogData)
    }
    getBlog()
  },[])

  const handleCreate = async (commentData) => {
    const newComment = await postComment(Number(id), commentData)
    setComments(prevState => [...prevState, newComment])
  }

  return (
    <div>
      {
        blog && <h3>{blog.id}</h3>
      }
      <div>
        {blog.comments &&
          blog.comments.map(comment => {
            <p>this is a comment</p>
          })
        }
      </div>
      <div>
        <button onClick={() => {setCommentToggle(true)}}></button>
        {
          commentToggle && 
          <CommentCreate handleCreate={handleCreate} id={id}/>
        }
      </div>
    </div>
  )
}
