import api from './apiConfig';


export const postComment = async (blogData) => {
  const resp = await api.post('/blogs', { blog: blogData });
  return resp.data;
}

export const destroyComment = async (id) => {
  const resp = await api.delete(`/blogs/${id}`);
  return resp;
}
